const current_day = new Date().getDay();
const is_weekday = current_day < 6 && current_day !== 0;

// count Friday as weekend and Sunday as weekday for night
const is_weekday_night = current_day !== 5 && current_day !== 6;

const current_time = new Date();

const midnight = new Date();
midnight.setHours(0);
midnight.setMinutes(0);
midnight.setMilliseconds(0);

const day_night_str = is_weekday ? 'day' : 'end';
const day_night_str_night = is_weekday_night ? 'day' : 'end';

let day_start = timeStringToDate(time[`day_start_week${day_night_str}`]);
let night_start = timeStringToDate(time[`night_start_week${day_night_str_night}`]);

const is_day_schedule = current_time >= day_start &&
                current_time < night_start;

const temp_target_schedule = away ? temp.away : (is_day_schedule ? temp.day : temp.night);

const temp_diff = Math.abs(temp_target_schedule - outside);
const breakpoints = [
    { diff: 20, adjust: 1, hours_to_cooldown: 5.5, hours_to_heatup: 2 },
    { diff: 14, adjust: 1, hours_to_cooldown: 6, hours_to_heatup: 3 },
    { diff: 7, adjust: 1, hours_to_cooldown: 7, hours_to_heatup: 5 },
    { diff: 4, adjust: 1, hours_to_cooldown: 9, hours_to_heatup: 7 },
    { diff: 2, adjust: 0.5, hours_to_cooldown: 14, hours_to_heatup: 9 }
];

const last_breakpoint = breakpoints[breakpoints.length - 1];
let temp_adjust = last_breakpoint.adjust;
let hours_to_cooldown = last_breakpoint.hours_to_cooldown;
let hours_to_heatup = last_breakpoint.hours_to_heatup;

for (let index = 0; index < breakpoints.length; index++){
    const breakpoint = breakpoints[index];
    if (breakpoint.diff <= temp_diff) {
        temp_adjust = breakpoint.adjust;
        hours_to_cooldown = breakpoint.hours_to_cooldown;
        hours_to_heatup = breakpoint.hours_to_heatup;
        break;
    } 
}

if (heat) {
    night_start.setHours(night_start.getHours() - hours_to_cooldown);
    if (night_start < midnight) {
        night_start.setDate(current_time.getDate());
    }
} else {
    day_start.setHours(day_start.getHours() - hours_to_heatup);
}

const is_day = current_time >= day_start &&
                current_time < night_start;
const ran_today = new Date(upstairs.temp.hvac_last_stopped) <= midnight;
const is_day_or_run = is_day || (is_day_schedule && ran_today);

const temp_target = away ? temp.away : 
    (is_day_or_run ? temp.day : temp.night);

let temp_adjusted = temp_target;

if (heat) {
    temp_adjusted = temp_target + temp_adjust;
} else {
    temp_adjusted = temp_target - temp_adjust;
}

let temp_adjusted_opposite_limit = temp_target + ((is_day ? 1 : 0.5) * (heat ? -1 : 1));

// if we already reach the target temp with adjustment (e.g 22 + 1.5)
// then set it back to the original target temp with 0.5 adjustment (e.g 22 - 0.5)
if ((heat && temp.current > temp_adjusted_opposite_limit) || 
    (!heat && temp.current < temp_adjusted_opposite_limit)) {
    temp_adjusted = temp_adjusted_opposite_limit;
}

// dinner -> downstairs -> cooler so we can raise the heat ceiling 
if (!heat) {
    const dinner_time_start = timeStringToDate(time.meal_start);

    const dinner_time_before = new Date(dinner_time_start);
    dinner_time_before.setHours(dinner_time_start.getHours() - 1);

