    const tv_off = tv_history.filter((x) => {
        const last_changed = new Date(x.last_changed);
        return x.state === 'off' &&
            last_changed >= dinner_time_tv_check
    });

    if (now > dinner_time_before && now < dinner_time_end && !tv_off) {
        temp_adjusted = temp_adjusted + 1;
    }
}

const tv_on = tv.state === "on";

let should_run = (!hvac_running || 
    (heat && temp_adjusted > temp.current) ||
    (!heat && temp_adjusted < temp.current)) &&
