function getHvacInfo(floor, states) {
    const thermostat = states[`climate.${floor}_thermostat`];
    const mode = thermostat.state;

    let temp = {};
    let state = {};
    let time = {};

    if (mode !== 'off') {
        temp.night = parseFloat(states[`input_number.${mode}_temp_night`].state);
        temp.day = parseFloat(states[`input_number.${mode}_temp_day`].state);
        temp.away = parseFloat(states[`input_number.${mode}_temp_away`].state);

        state.manual_control = states[`input_boolean.${floor}_thermostat_manual_control`].state === 'on';
        time.meal_start = states['input_datetime.heat_meal_start'].state;
        state.tv = states['media_player.living_room_tv'];

        if (floor === 'downstairs') {
            state.light = states['switch.cabinet_light'].state;

            if (mode === 'heat') {
                temp.meal = parseFloat(states[`input_number.${mode}_temp_meal`].state);
            }
        }

        if (floor === 'upstairs') {
            time.day_start_weekday = states[`input_datetime.${mode}_day_start_weekday`].state;
            time.day_start_weekend = states[`input_datetime.${mode}_day_start_weekend`].state;
            time.night_start_weekday = states[`input_datetime.${mode}_night_start_weekday`].state;
            time.night_start_weekend = states[`input_datetime.${mode}_night_start_weekend`].state;
        }
    }

    temp.current = parseFloat(thermostat.attributes.current_temperature);
    temp.target = parseFloat(thermostat.attributes.temperature);

    state.hvac = states[`sensor.${floor}_thermostat_hvac_action`].state;
    state.hvac_mode = mode;
    state.fan = states[`sensor.${floor}_thermostat_fan_state`].state;
    state.fan_mode = thermostat.attributes.fan_mode;
    state.heat = state.hvac_mode === 'heat';
    return { temp, time, state }
}

