const entity = env.get('entity_id');
const payloadEntity = msg.payload.event.service_data.entity_id;
const payloadDomain = msg.payload.event.domain;

const correctEntity = Array.isArray(payloadEntity) ? payloadEntity.includes(entity) : payloadEntity === entity;
const correctDomain = payloadDomain === 'switch' || payloadDomain === 'light';
return { 
    payload: correctEntity && correctDomain
};

const states = global.get('homeassistant.homeAssistant.states');
const url = states["sensor.mail_image_url"].state;

return {
    "payload": {
    "notification": {
        "title": "Mails & Packages",
        "message": `✉️ ${msg.payload} New mail(s) coming today (around 1 PM)`,
        "data": {
            "tag": "mail.coming",
            "image": url
        }
    },
    "log": {
        "name": `USPS (${msg.payload})`,
        "entity_id": "sensor.mail_usps_mail",
        "message": "notified coming today",
        "domain": "notify"
    }
}
}

return {
    "payload": {
        "notification": {
            "title": "IP address changed",
            "message": `🌐 ⚠️ Your IP address is now ${msg.payload}`,
            "data": {
                "tag": "internet.ip.warn",
                "priority": "high",
                "ttl": 0
            }
        },
        "log": {
            "name": "IP address (Node Red)",
            "entity_id": "sensor.ip_address",
            "message": "notified changed",
            "domain": "notify"
        }
    }
}

if (msg && msg.payload && msg.payload.notification) {
    let data = msg.payload.notification.data || {};
    if (!data.clickAction) {
        msg.payload.notification.data = Object.assign(data, { clickAction: '/lovelace-test/default_view' });
    }
}
return msg;

return {
    "payload": {
    "notification": {
        "title": "Water plant",
        "message": `🌱 Your plant (${msg.data.attributes.friendly_name}) needs a drink soon!`,
        "data": {
            "tag": `plant.water.${msg.data.entity_id}`,
            "actions": [
                {
                    "title": "Done!",
                    "action": `notification.plant.${msg.data.entity_id}.watered`
                },
                {
                    "title": "Remind me later",
                    "action": `notification.plant.${msg.data.entity_id}.remind`
                }
            ],
            "clickAction": "/lovelace-test/plants"
        }
    },
    "log": {
        "name": `Plant (${msg.data.attributes.friendly_name})`,
        "entity_id": `${msg.data.entity_id}`,
        "message": "notified needs watering",
        "domain": "notify"
    }
}
}

const entity = msg.data.entity_id;
const dateEntity = entity.replace('input_boolean', 'input_datetime');
if (msg.topic === 'datetime') {
    return null;
}

if (msg.old_state === 'on') {
    return null;
}

return {
    "entity_id": dateEntity,
}

const entity = msg.data.entity_id;
const dateEntity = entity.replace('input_boolean', 'input_datetime');
const durationEntity = entity.replace('input_boolean', 'input_number');

if (!durationEntity) {
    node.error(`Not found or zero ${entity}`);
}

if (durationEntity) {
    return [{
        data: msg.data,
        entity_id: entity,
        date_entity: dateEntity,
        duration_entity: durationEntity,
    }, null];
}
return [null, null];

const current = new Date();
const whenToWater = new Date(msg.date_payload);
// convert to seconds
const duration = parseFloat(msg.duration_payload) * 24 * 60 * 60;

// minus 6 hours so it can notify earlier
whenToWater.setSeconds(whenToWater.getSeconds() + duration - 60*60*6);

if (whenToWater <= current) {
    const day = current.getDay();
    const hour = current.getHours();
    const isWeekend = (day === 6) || (day === 0);
    const isWeekdayTime = hour >= 18 && hour <= 22;
    const isWeekendTime = hour >= 9 && hour <= 23;

    if ((isWeekend && isWeekendTime) || (!isWeekend && isWeekdayTime)) {
        return [{
            data: msg.data,
            entity_id: msg.entity_id,
        }, null];
    }
}
return [null, null];

return {
    data: {
        entity_id: msg.data.entity_id
    },
    reset: true
};

const entity = msg.data.entity_id;
const booleanEntity = entity.replace('input_datetime', 'input_boolean');
return {
    topic: 'datetime',
    data: {
        "entity_id": booleanEntity   
    }
}

const now = new Date();
let cutoff = new Date();
cutoff.setHours(23);
cutoff.setMinutes(0);
cutoff.setSeconds(0);
cutoff.setMilliseconds(0);

let cutoffMorning = new Date();
cutoffMorning.setHours(5);
cutoffMorning.setMinutes(0);
cutoffMorning.setSeconds(0);
cutoffMorning.setMilliseconds(0);


if (now > cutoff || now < cutoffMorning) {
    return [{ payload: true }, null]
}

return [null, { payload: true }];

const action = msg.payload.event.action;
const re = /notification\.plant\.(input_boolean\..*)\..*/;
const groups = re.exec(action);
if (groups && groups.length > 0) {
    if (action.includes("watered")) {
        return [{ payload: groups[1] }, null];
    }

    if (action.includes("remind")) {
        return [null, { payload: groups[1] }];
    }

}
return [null, null];

const now = new Date();
let cutoff = new Date();
cutoff.setHours(23);
cutoff.setMinutes(59);
cutoff.setSeconds(0);
cutoff.setMilliseconds(0);

let cutoffMorning = new Date();
cutoffMorning.setHours(11);
cutoffMorning.setMinutes(0);
cutoffMorning.setSeconds(0);
cutoffMorning.setMilliseconds(0);


if (now < cutoff || now > cutoffMorning) {
    return [{ payload: true }, null]
}

return [null, { payload: true }];

const after = new Date(msg.data.event.new_state.last_changed);
const before = new Date(msg.data.event.old_state.last_changed);

msg.custom = {
    time_passed_minutes: (after - before) / 1000 / 60
};

return msg;

const entity = msg.data.entity_id;
const booleanEntity = entity.replace('input_number', 'input_boolean');
return {
    topic: 'number',
    data: {
        "entity_id": booleanEntity   
    }
}

const now = Date.now();
const lastFilled = new Date(msg.payload).getTime();
// convert to hours
const duration = parseFloat(now - lastFilled) / (1000 * 60 * 60);

if (duration > 36) {
    return {
        payload: true
    };
}
return null;
